% ---------------------------------------------------------------
% code
% ---------------------------------------------------------------

    
    shift = shiftOld;
    
    % First identify the start and end indices of existing data
    shift([1,end]) = nan; % force first and last value to nan

    % Remove sections with steps or big changes in gradient.
    dShift = [0 ; diff(shift)]; % 0 prepended so the dShift pins onto the 
    shift(dShift>1) = nan;      % secondvalue that makes up the gradient.





    isVal = ~isnan(shift);
    
    if ~any(isVal); shift = zeros(size(shift)); return; end

    isValAfter = [isVal(2:end);false];
    isValBefore = [false; isVal(1:(end-1))];

    iStart  = find(isVal & ~isValBefore);
    iFinish = find(isVal & ~isValAfter);

    % First, we want to check that we could join the finish of each section to
    % the start of the next without exceeding a gradient of +/- 1
    for i = (1:(numel(iFinish)-1)) %go through all the blanks in turn

        tDiff = iStart(i+1) - iFinish(i);   if tDiff<0; error(); end
        shiftDiff = shift(iStart(i+1)) - shift(iFinish(i));

        if shiftDiff>tDiff %then the gradient is changing too fast
            
            trim = ceil((shiftDiff - tDiff)/2);
            % We need to chop this amount of data off each side of the 
            % blank, but we can't if there isn't enough data.

            lengthBefore = iFinish(i) - iStart(i);
            lengthAfter = iFinish(i) - iStart(i);

            if lengthBefore > trim
                iFinish(i) = iFinish(i)-trim;
            else
                iStart(i) = nan;
                iFinish(i) = nan;
            end
            
            if lengthAfter > trim
                iStart(i+1) = iStart(i+1)+trim;
            else
                iStart(i+1) = nan;
                iFinish(i+1) = nan;
            end
        end

    end
    
    iStart(isnan(iStart)) = [];
    iFinish(isnan(iFinish)) = [];
    
    % Now fill in the bits between data.
    % Create first and last points on 'zero order hold'
    iStart = [1 ; iStart ; numel(shift)];
    iFinish = [1 ; iFinish ; numel(shift)];
    shift(iStart(end)) = shift(iStart(end-1));
    shift(iFinish(1)) = shift(iFinish(2));

    
    for i = (1:(numel(iFinish)-1)) %go through all the blanks in turn again
        tDiff = iStart(i+1) - iFinish(i);   if tDiff<0; error(); end
        shiftDiff = shift(iStart(i+1)) - shift(iFinish(i));
        
        shift(iFinish(i)+(1:tDiff)) = ...
                            shift(iFinish(i)) + (1:tDiff)* shiftDiff/tDiff;
                        
    end
        
