    for i = (1:(numel(iFinish)-1)) %go through all the blanks in turn

	% Time difference across the gap (in indices)
        tDiff = iStart(i+1) - iFinish(i);   if tDiff<0; error(); end
        shiftDiff = shift(iStart(i+1)) - shift(iFinish(i));

        if shiftDiff>tDiff %then the gradient is changing too fast
            
            trim = ceil((shiftDiff - tDiff)/2);
            % We need to chop this amount of data off each side of the 
            % blank, but we can't if there isn't enough data.

            lengthBefore = iFinish(i) - iStart(i);
            lengthAfter = iFinish(i+1) - iStart(i+1); % Previously set to iFinish(i) - iStart(i) by mistake?

